import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'

import Navigation from './components/Navigation'
import Layout from './components/GeneralLayout'

import React, { Component } from 'react';

class Proveedores extends Component {
    constructor ()
    {
        super();
        this.state = {
            email: '',
            message: ''
        };

        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(e)
    {
        e.preventDefault();
        console.log(this.state)
        this.setState({
            title: '',
            responsible: '',
            description: '',
            priority: 'low'
        });
    }

    handleInputChange(e)
    {
        const {value, name} = e.target;
        console.log(value, name);
        this.setState({
            [name]: value
        });
    }

    render() {
        return (
            <div>
                <Navigation />
                <Layout>
                    <h1 className="text-center pb-3">Contacto</h1>

                    <div className="row col-6 row pt-4 mx-auto">
                        { /*
                            Formulario de proveedores (Izquierda)
                        */ }
                        <Form className="col-xs-2 col-sm-3 col-md-4 col-lg-7 col-xl-8" onSubmit={this.handleSubmit}>
                            <Form.Group controlId="formContact.Email">
                                <Form.Control type="email" placeholder="Email" />
                            </Form.Group>
                            <Form.Group controlId="formContact.Mensaje">
                                <Form.Control as="textarea" rows="4" placeholder="Escriba aquí su mensaje" />
                            </Form.Group>
                            <Button variant="dark" type="submit" onClick={(e) => {e.preventDefault()}}>
                                Enviar
                            </Button>
                        </Form>
                        { /*
                            Información de contacto (Derecha)
                        */ }
                        <div className="col-xs-2 col-sm-3 col-md-4 col-lg-5 col-xl-4 ">
                            <div className="justify-content-center">
                                <div>
                                    <h6>Zona Recreativa CR</h6>

                                    <p className="text-secondary">
                                        acme.info@2mail.com
                                    </p>
                                    <p className="text-secondary">
                                        +0 000-000-0000
                                    </p>
                                    { /*<h6>FOLLOW US</h6>*/ }

                                    { /*
                                        Enlaces externos
                                    */ }
                                </div>
                            </div>
                        </div>
                    </div>
                </Layout>
            </div>
        );
    }
}

export default Proveedores;
